-- Create external data source
CREATE EXTERNAL DATA SOURCE MyDataSource
WITH (
    LOCATION = 'https://ds598team3.dfs.core.windows.net/data'
);
GO

-- Create external file format
CREATE EXTERNAL FILE FORMAT ParquetFileFormat
WITH (
FORMAT_TYPE = PARQUET
);
GO

-- Create external table ExternalBluebike
CREATE EXTERNAL TABLE ExternalBluebike(
    date DATE, 
    riders INT
)
WITH (
    LOCATION = 'gold/agg_bluebike/parquet/bluebike_agg.parquet',
    DATA_SOURCE = MyDataSource,
    FILE_FORMAT = ParquetFileFormat
);
GO
